mapper.select:
select team_apply_log.ids, team_apply_log_id, team_apply_log.team_id, team.manager_id, team_manager.name as team_manager_name, apply_user_id, apply_user.name as apply_user_name, process_user_id, process_user.name as process_user_name, team_apply_log.create_time, team.name as team_name, team_apply_log.status, apply_remark, process_remark from ((((team_apply_log left join team on team.team_id = team_apply_log.team_id) left join user_info apply_user on apply_user_id = user_id) left join user_info process_user on process_user_id = process_user.user_id) left join user_info team_manager on manager_id=team_manager.user_id) where 1 = 1
and apply_user_id=#{applyUserId}
and process_user_id=#{processUserId}
and team_apply_log.status=#{status}
order by create_time desc limit #{offset},#{size}

select count(*) as totalUnProcess
from team_apply_log
where 1=1

    and apply_user_id = #{userId}


    and team_id=#{teamId}

and status='PENDING'

mapper.update:
update team_apply_log set
read_time=#{readTime}
where team_apply_log_id=#{teamApplyLogId}

update apply_team set
status=#{status},
process_remark=#{processRemark},
process_time=#{processTime},
process_user_id=#{processUserId}
where apply_team_log_id=#{applyTeamLogId}

mapper.select:
select count(*) as total from team_apply_log
where read_time is null

and apply_user_id<>#{userId}

and team_id in

    #{item}
