mapper.select:
select my_team.ids, my_team.user_id, my_team.team_id, team.name as team_name, me.name as my_name, manager.name as manager_name, creator.name as creator_name, my_team.status from (((my_team inner join team on my_team.team_id = team.team_id) inner join user_info me on my_team.user_id = me.user_id) inner join user_info manager on team.manager_id = manager.user_id) inner join user_info creator on team.create_user_id = creator.user_id where 1 = 1
and me.user_id = #{userId}
and my_team.status = #{status}
and my_team.team_id = #{teamId}

select count(*) as totalUnProcess
from apply_team
where 1=1

    and apply_user_id = #{userId}


    and apply_team_id=#{teamId}

and process_result is null

mapper.update:
update apply_team set
read_time=#{readTime}
where apply_team_log_id=#{applyTeamLogId}

update apply_team set
process_result=#{processResult},
process_remark=#{processRemark},
process_time=#{processTime},
process_user_id=#{processUserId}
where apply_team_log_id=#{applyTeamLogId}
