mapper.select:
select team.ids, team_id, team.name as team_name, description, team.create_time, create_user_id, creator.name as create_user_name, team.status, manager_id, manager.name as manager_name from (team left join user_info creator on create_user_id=user_id) left join user_info manager on manager_id=manager.user_id where 1=1
and team_id=#{teamId}
and team.name=#{teamName}

select team.ids, team_id, team.name as team_name, description, team.create_time, create_user_id, creator.name as create_user_name, team.status, manager_id, manager.name as manager_name from (team left join user_info creator on create_user_id=user_id) left join user_info manager on manager_id=manager.user_id where 1=1
and create_user_id=#{createUserId}
and team.status=#{status}
and team_id=#{teamId}
and manager_id=#{managerId}
and team.name like '%'+#{teamName}+'%'
order by team.ids desc limit #{offset},#{size}

mapper.update:
update team


        name=#{name},


        description=#{description}


where team_id=#{teamId}
