mapper.select:
select volunteer_apply.ids, volunteer_apply_id, volunteer_task.volunteer_task_id, title, volunteer_apply.create_time, apply_user_id, apply_user.name as apply_user_name, remark, read_time, process_result, process_time, process_remark, process_user_id, process_user.name as process_user_name, process_read_time, title from volunteer_apply left join volunteer_task on volunteer_apply.volunteer_task_id=volunteer_task.volunteer_task_id left join user_info apply_user on apply_user_id=user_id left join user_info process_user on process_user_id=process_user.user_id where 1=1
and create_user_id=#{createUserId}
and apply_user_id=#{applyUserId}
order by volunteer_apply.ids desc

mapper.update:
update volunteer_apply set
process_result=#{processResult},
process_time=#{processTime},
process_remark=#{processRemark},
process_user_id=#{processUserId}
where volunteer_apply_id=#{volunteerApplyId}
