mapper.select:
select volunteer_task.ids, volunteer_task_id, volunteer_task.create_time, create_user_id, name as create_user_name, title, content, start_time, pid, views, applies, volunteer_task.status from volunteer_task left join user_info create_user on create_user_id=user_id where 1=1
and create_user_id=#{createUserId}
and volunteer_task.status=#{status}
order by create_user.ids desc limit #{offset},#{size}
